mapper.update:
UPDATE opener_code
SET
CODE_CODE = #{code}
,CODE_INFO = #{info}
,CODE_CONTENT = #{content}
,CODE_LUSER = #{userId}
,CODE_LDATE = SYSDATE()
,CODE_USE = #{ck}
WHERE CODE_CODE = #{prev}

mapper.select:
SELECT (SELECT COUNT(*) FROM opener_notice LEFT JOIN opener_code CODE1 ON CODE1.CODE_CODE = NOTICE_IMPORT LEFT JOIN opener_code CODE2 ON CODE2.CODE_CODE = NOTICE_TYPE WHERE NOTICE_TYPE = #{type}
AND NOTICE_IMPORT = #{import}
AND NOTICE_TITLE LIKE(CONCAT('%', #{keyWord}, '%'))
AND DATE_FORMAT(NOTICE_FDATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
) AS TOTAL, NOTICE_KEY, NOTICE_IMPORT, NOTICE_TYPE, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_FUSER, DATE_FORMAT(NOTICE_FDATE, '%Y-%m-%d %H:%i') AS NOTICE_FDATE, NOTICE_LUSER, NOTICE_LDATE, NOTICE_REAL_FILENAME, NOTICE_FILENAME, NOTICE_FILEPATH, NOTICE_VIEWS, CODE1.CODE_CONTENT AS IMPORT, CODE2.CODE_CONTENT AS TYPE, ADMIN.ADMIN_NAME FROM opener_notice LEFT JOIN opener_code CODE1 ON CODE1.CODE_CODE = NOTICE_IMPORT LEFT JOIN opener_code CODE2 ON CODE2.CODE_CODE = NOTICE_TYPE LEFT JOIN opener_user_admin ADMIN ON ADMIN.ADMIN_KEY = NOTICE_FUSER WHERE NOTICE_TYPE = #{type}
AND NOTICE_IMPORT = #{import}
AND NOTICE_TITLE LIKE(CONCAT('%', #{keyWord}, '%'))
AND DATE_FORMAT(NOTICE_FDATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
order by notice_fdate desc LIMIT ${page}, ${pageSize}

mapper.update:
UPDATE opener_notice SET
NOTICE_IMPORT = #{import}
,NOTICE_TYPE = #{type}
,NOTICE_TITLE = #{title}
,NOTICE_CONTENT = #{cont}
,NOTICE_LUSER = #{userKey}
,NOTICE_LDATE = SYSDATE()
,NOTICE_REAL_FILENAME = #{realName}
,NOTICE_FILENAME = #{fileName}
,NOTICE_FILEPATH = #{filePath}
WHERE NOTICE_KEY = #{noticeKey}

UPDATE opener_notice SET
NOTICE_VIEWS = #{view}
WHERE NOTICE_KEY = #{key}

mapper.select:
SELECT (SELECT COUNT(*) FROM opener_notice LEFT JOIN opener_code CODE1 ON CODE1.CODE_CODE = NOTICE_IMPORT LEFT JOIN opener_code CODE2 ON CODE2.CODE_CODE = NOTICE_TYPE WHERE NOTICE_TYPE = #{type} AND NOTICE_USE = '1'
AND NOTICE_IMPORT = #{import}
AND NOTICE_TITLE LIKE(CONCAT('%', #{keyWord}, '%'))
AND DATE_FORMAT(NOTICE_FDATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
) AS TOTAL, NOTICE_KEY, NOTICE_IMPORT, NOTICE_TYPE, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_FUSER, DATE_FORMAT(NOTICE_FDATE, '%Y-%m-%d %H:%i') AS NOTICE_FDATE, NOTICE_LUSER, NOTICE_LDATE, NOTICE_REAL_FILENAME, NOTICE_FILENAME, NOTICE_FILEPATH, CODE1.CODE_CONTENT AS IMPORT, CODE2.CODE_CONTENT AS TYPE, ADMIN.ADMIN_NAME, NOTICE_VIEWS FROM opener_notice LEFT JOIN opener_code CODE1 ON CODE1.CODE_CODE = NOTICE_IMPORT LEFT JOIN opener_code CODE2 ON CODE2.CODE_CODE = NOTICE_TYPE LEFT JOIN opener_user_admin ADMIN ON ADMIN.ADMIN_KEY = NOTICE_FUSER WHERE NOTICE_TYPE = #{type} AND NOTICE_USE = '1'
AND NOTICE_IMPORT = #{import}
AND NOTICE_TITLE LIKE(CONCAT('%', #{keyWord}, '%'))
AND DATE_FORMAT(NOTICE_FDATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
order by notice_fdate desc LIMIT ${page}, ${pageSize}
