mapper.select:
SELECT MQTT_LIST_KEY, MQTT_LIST_APART, MQTT_LIST_DCOD, IFNULL(MQTT_LIST_FLOOR,'') AS MQTT_LIST_FLOOR, IFNULL(CODE2.CODE_CONTENT,'') AS FLOOR_NAME, MQTT_LIST_NAME, MQTT_LIST_NUM, MQTT_LIST_USE, CODE.CODE_CONTENT, IFNULL(DATE_FORMAT(CTIME.LOG_CMD_TIME, '%Y-%m-%d %H:%i'), '') AS START_TIME,
IFNULL(IF(CONNECT.LOG_CMD_TIME < CTIME.LOG_CMD_TIME, DATE_FORMAT(CTIME.LOG_CMD_TIME, '%Y-%m-%d %H:%i'), DATE_FORMAT(CONNECT.LOG_CMD_TIME, '%Y-%m-%d %H:%i')), '') AS CONNECT_TIME FROM mqtt_list
RIGHT JOIN OPENER_USER_ADDR ON MQTT_LIST_DCOD = USER_ADDR AND USER_ID = #{userID}
LEFT JOIN opener_code CODE ON CODE.CODE_CODE = MQTT_LIST_DCOD LEFT JOIN opener_code CODE2 ON CODE2.CODE_CODE = MQTT_LIST_FLOOR LEFT JOIN ( SELECT LOG_CMD_NUM, LOG_CMD_TIME, LOG_CMD_CMD FROM mqtt_cmd_log WHERE (LOG_CMD_NUM, LOG_CMD_TIME, LOG_CMD_CMD) in( select LOG_CMD_NUM, max(LOG_CMD_TIME) as LOG_CMD_TIME, LOG_CMD_CMD from mqtt_cmd_log
where LOG_CMD_CMD = '255' group by LOG_CMD_NUM ) group by LOG_CMD_TIME ORDER BY LOG_CMD_KEY DESC ) CTIME ON CTIME.LOG_CMD_NUM = MQTT_LIST_NUM LEFT JOIN ( SELECT LOG_CMD_NUM, LOG_CMD_TIME, LOG_CMD_CMD FROM mqtt_cmd_log WHERE (LOG_CMD_NUM, LOG_CMD_TIME, LOG_CMD_CMD) in( select LOG_CMD_NUM, max(LOG_CMD_TIME) as LOG_CMD_TIME, LOG_CMD_CMD from mqtt_cmd_log where LOG_CMD_CMD = '1' group by LOG_CMD_NUM ) group by LOG_CMD_TIME ORDER BY LOG_CMD_KEY DESC ) CONNECT ON CONNECT.LOG_CMD_NUM = MQTT_LIST_NUM WHERE 1=1 AND MQTT_LIST_APART = #{hotelCode}
AND MQTT_LIST_DCOD = #{dcod}
AND MQTT_LIST_FLOOR = #{floor}
order by MQTT_LIST_DCOD,MQTT_LIST_FLOOR,MQTT_LIST_KEY

SELECT MQTT_LIST_KEY, MQTT_LIST_APART, MQTT_LIST_DCOD, IFNULL(CODE.CODE_CONTENT,'') AS DCOD_NAME, MQTT_LIST_FLOOR, IFNULL(CODE2.CODE_CONTENT,'') AS FLOOR_NAME, MQTT_LIST_NAME, MQTT_LIST_NUM, IFNULL(LIKE_LIKE,"2") as LIKE_LIKE FROM mqtt_list
RIGHT JOIN opener_user_addr_like ON LIKE_ADDR = MQTT_LIST_KEY AND LIKE_USER_ID = #{userId} AND LIKE_LIKE = '1'
LEFT JOIN opener_user_addr_like ON LIKE_ADDR = MQTT_LIST_KEY AND LIKE_USER_ID = #{userId}
LEFT JOIN opener_code CODE ON CODE.CODE_CODE = MQTT_LIST_DCOD LEFT JOIN opener_code CODE2 ON CODE2.CODE_CODE = MQTT_LIST_FLOOR WHERE MQTT_LIST_APART = #{apart}
and MQTT_LIST_DCOD = #{dcod}
and MQTT_LIST_USE=1 ORDER BY MQTT_LIST_DCOD, MQTT_LIST_FLOOR, MQTT_LIST_KEY

SELECT USER_ID, USER_ADDR
FROM OPENER_USER_ADDR
WHERE USER_ID = #{userID}

SELECT USER_ID, USER_ADDR
FROM OPENER_USER_ADDR
WHERE USER_ID = #{userID}

mapper.delete:
DELETE addr FROM 
opener_user_addr as addr
join mqtt_list as mqtt
on addr.user_addr = mqtt.mqtt_list_key
WHERE addr.USER_ID = #{userID}
and mqtt.mqtt_list_dcod = #{dcod}

DELETE FROM opener_user_addr_like 
WHERE LIKE_USER_ID = #{userId} 
AND LIKE_ADDR = #{addr}

mapper.update:
UPDATE mqtt_action SET
    MQTT_ACTION_ANSDATE = SYSDATE()
WHERE MQTT_ACTION_KEY = #{insertId}

mapper.select:
SELECT
IFNULL(MAX(LOG_CMD_KEY), 0) + 1 AS LOG_CMD_KEY
FROM mqtt_cmd_log;

(SELECT MQTT_ACTION_KEY, MQTT_ACTION_NUM, DATE_FORMAT(MQTT_ACTION_REQDATE, '%y.%m.%d %T') AS MQTT_ACTION_REQDATE, MQTT_ACTION_ANSDATE, MQTT_ACTION_CMD, MQTT_ACTION_USER, MQTT_ACTION_LOG, IFNULL(TIMESTAMPDIFF(SECOND, MQTT_ACTION_REQDATE, MQTT_ACTION_ANSDATE), '99') AS DIFF, IFNULL(CODE1.CODE_CONTENT,'') AS DCOD_NAME, IFNULL(CODE2.CODE_CONTENT,'') AS FLOOR_NAME, MQTT_LIST_NAME, USER.USER_NAME AS USER_NAME, ROOM.ROOM_CODE_NUM AS ROOM_NUM, CODE.CODE_CONTENT AS CODE_CONTENT, IFNULL(OPEN.CODE_CONTENT, '스마트키') AS OPEN_TYPE FROM mqtt_action LEFT JOIN opener_user USER ON USER.USER_ID = MQTT_ACTION_USER LEFT JOIN FO_ROOM_CODE ROOM ON ROOM.ROOM_CODE_CODE = USER.USER_ADDR2 LEFT JOIN opener_code CODE ON CODE.CODE_CODE = USER.USER_ADDR1 LEFT JOIN opener_code OPEN ON OPEN.CODE_CODE = MQTT_ACTION_OPEN LEFT JOIN MQTT_LIST ON MQTT_LIST_NUM = MQTT_ACTION_NUM LEFT JOIN opener_code CODE1 ON CODE1.CODE_CODE = MQTT_LIST_DCOD LEFT JOIN opener_code CODE2 ON CODE2.CODE_CODE = MQTT_LIST_FLOOR WHERE MQTT_ACTION_CMD = '2' AND USER.USER_KEY = #{userKey}
AND USER.USER_ID = #{subUser}
AND DATE_FORMAT(MQTT_ACTION_REQDATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
) UNION ALL (SELECT MQTT_ACTION_KEY, MQTT_ACTION_NUM, DATE_FORMAT(MQTT_ACTION_REQDATE, '%y.%m.%d %T') AS MQTT_ACTION_REQDATE, MQTT_ACTION_ANSDATE, MQTT_ACTION_CMD, MQTT_ACTION_USER, MQTT_ACTION_LOG, IFNULL(TIMESTAMPDIFF(SECOND, MQTT_ACTION_REQDATE, MQTT_ACTION_ANSDATE), '99') AS DIFF, IFNULL(CODE1.CODE_CONTENT,'') AS DCOD_NAME, IFNULL(CODE2.CODE_CONTENT,'') AS FLOOR_NAME, MQTT_LIST_NAME, USER.USER_SUB_NAME AS USER_NAME, ROOM.ROOM_CODE_NUM AS ROOM_NUM, CODE.CODE_CONTENT AS CODE_CONTENT, IFNULL(OPEN.CODE_CONTENT, '스마트키') AS OPEN_TYPE FROM mqtt_action LEFT JOIN opener_user_sub USER ON USER.USER_SUB_ID = MQTT_ACTION_USER LEFT JOIN opener_user USER2 ON USER.USER_KEY = USER2.USER_KEY LEFT JOIN FO_ROOM_CODE ROOM ON ROOM.ROOM_CODE_CODE = USER2.USER_ADDR2 LEFT JOIN opener_code CODE ON CODE.CODE_CODE = USER2.USER_ADDR1 LEFT JOIN opener_code OPEN ON OPEN.CODE_CODE = MQTT_ACTION_OPEN LEFT JOIN MQTT_LIST ON MQTT_LIST_NUM = MQTT_ACTION_NUM LEFT JOIN opener_code CODE1 ON CODE1.CODE_CODE = MQTT_LIST_DCOD LEFT JOIN opener_code CODE2 ON CODE2.CODE_CODE = MQTT_LIST_FLOOR WHERE MQTT_ACTION_CMD = '2' AND USER.USER_KEY = #{userKey}
AND USER.USER_SUB_ID = #{subUser}
AND DATE_FORMAT(MQTT_ACTION_REQDATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
) ORDER BY MQTT_ACTION_REQDATE DESC LIMIT ${page},${pageSize}

SELECT SUM(CNT) AS COUNT FROM ( SELECT COUNT(*) AS CNT FROM mqtt_action LEFT JOIN opener_user USER ON USER.USER_ID = MQTT_ACTION_USER WHERE MQTT_ACTION_CMD = '2' AND USER.USER_KEY = #{userKey}
AND USER.USER_ID = #{subUser}
AND DATE_FORMAT(MQTT_ACTION_REQDATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
UNION ALL SELECT COUNT(*) AS CNT FROM mqtt_action LEFT JOIN opener_user_sub USER ON USER.USER_SUB_ID = MQTT_ACTION_USER WHERE MQTT_ACTION_CMD = '2' AND USER.USER_KEY = #{userKey}
AND USER.USER_SUB_ID = #{subUser}
AND DATE_FORMAT(MQTT_ACTION_REQDATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
) AS COUNT

mapper.update:
UPDATE mqtt_list SET
MQTT_LIST_DCOD = #{dcod}
,MQTT_LIST_FLOOR = #{floor}
,MQTT_LIST_NUM = #{num}
,MQTT_LIST_NAME = #{name}
,MQTT_LIST_LUSER = #{userId}
,MQTT_LIST_LDATE =SYSDATE()
,MQTT_LIST_USE = #{ck}
WHERE MQTT_LIST_KEY = #{code}
AND MQTT_LIST_APART = #{hotelCode}

UPDATE fo_room_door
SET
ROOM_DOOR_DATE = SYSDATE()
,ROOM_PASS_USER = #{pass}
,ROOM_PASS_CLEANER = #{cleaner}
,ROOM_PASS_MASTER = #{master}
,ROOM_TIME_DOOR = #{doorTime}
,ROOM_TIME_PASS = #{passTime}
,ROOM_DOOR_USER = #{user}
,ROOM_DOOR_ADMIN = #{admin}
WHERE ROOM_DOOR_RONO = #{rono}
AND ROOM_DOOR_BS = #{bsCode}
