mapper.select:
SELECT (select count(*) from mqtt_action LEFT JOIN bridge_code OPEN ON OPEN.CODE_CODE = MQTT_ACTION_OPEN LEFT JOIN MQTT_LIST ON MQTT_LIST_NUM = MQTT_ACTION_NUM LEFT JOIN bridge_code CODE ON CODE.CODE_CODE = MQTT_LIST_DCOD LEFT JOIN bridge_code CODE2 ON CODE2.CODE_CODE = MQTT_LIST_FLOOR LEFT JOIN ( SELECT BRIDGE_TRAN_ACNO, BRIDGE_TRAN_RSPK, BRIDGE_TRAN_GNAME FROM fo_bridge_tran ) TRAN ON TRAN.BRIDGE_TRAN_ACNO = SUBSTRING_INDEX(MQTT_ACTION_USER, '/', 1) AND TRAN.BRIDGE_TRAN_RSPK = SUBSTRING_INDEX(MQTT_ACTION_USER, '/', -1) LEFT JOIN( SELECT ADMIN_NAME, ADMIN_ID, ADMIN_KEY FROM co_user_admin ) USER ON USER.ADMIN_ID = MQTT_ACTION_USER WHERE MQTT_ACTION_HOTEL = #{bsCode} AND MQTT_ACTION_NUM = #{rono}
AND OPEN.CODE_CODE = #{code}
ORDER BY MQTT_ACTION_REQDATE DESC ) as counts, MQTT_ACTION_KEY, MQTT_ACTION_NUM, DATE_FORMAT(MQTT_ACTION_REQDATE, '%y.%m.%d %T') AS MQTT_ACTION_REQDATE, MQTT_ACTION_ANSDATE, MQTT_ACTION_CMD, MQTT_ACTION_USER, MQTT_ACTION_LOG, IFNULL(CODE.CODE_CONTENT,'') AS DCOD_NAME, IFNULL(CODE2.CODE_CONTENT,'') AS FLOOR_NAME, MQTT_LIST_NAME, IFNULL(TIMESTAMPDIFF(SECOND, MQTT_ACTION_REQDATE, MQTT_ACTION_ANSDATE), '99') AS DIFF, IFNULL(OPEN.CODE_CONTENT, 'μ¤λ§νΈν‚¤') AS OPEN_TYPE, MQTT_ACTION_STATE, TRAN.BRIDGE_TRAN_ACNO, CASE WHEN TRAN.BRIDGE_TRAN_ACNO IS NULL THEN USER.ADMIN_NAME ELSE TRAN.BRIDGE_TRAN_GNAME END AS BRIDGE_TRAN_GNAME FROM mqtt_action LEFT JOIN bridge_code OPEN ON OPEN.CODE_CODE = MQTT_ACTION_OPEN LEFT JOIN MQTT_LIST ON MQTT_LIST_NUM = MQTT_ACTION_NUM LEFT JOIN bridge_code CODE ON CODE.CODE_CODE = MQTT_LIST_DCOD LEFT JOIN bridge_code CODE2 ON CODE2.CODE_CODE = MQTT_LIST_FLOOR LEFT JOIN ( SELECT BRIDGE_TRAN_ACNO, BRIDGE_TRAN_RSPK, BRIDGE_TRAN_GNAME FROM fo_bridge_tran ) TRAN ON TRAN.BRIDGE_TRAN_ACNO = SUBSTRING_INDEX(MQTT_ACTION_USER, '/', 1) AND TRAN.BRIDGE_TRAN_RSPK = SUBSTRING_INDEX(MQTT_ACTION_USER, '/', -1) LEFT JOIN( SELECT ADMIN_NAME, ADMIN_ID, ADMIN_KEY FROM co_user_admin ) USER ON USER.ADMIN_ID = MQTT_ACTION_USER WHERE MQTT_ACTION_HOTEL = #{bsCode} AND MQTT_ACTION_NUM = #{rono}
AND OPEN.CODE_CODE = #{code}
ORDER BY MQTT_ACTION_REQDATE DESC LIMIT ${page},${pageSize}
