mapper.select:
SELECT (select count(*) from fo_api_log WHERE API_LOG_BS = #{bsCode}
AND API_LOG_TYPE = #{type}
AND DATE_FORMAT(API_LOG_DATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
AND API_LOG_GNAME LIKE CONCAT('%', #{gname}, '%')
AND API_LOG_ACNO LIKE CONCAT('%', #{acno}, '%')
ORDER BY API_LOG_DATE DESC ) as counts, API_LOG_KEY, API_LOG_SEQ, API_LOG_BS, API_LOG_ACNO, API_LOG_RONO, DATE_FORMAT(API_LOG_IDATE, '%Y-%m-%d %p %H:%i') AS API_LOG_IDATE, DATE_FORMAT(API_LOG_ODATE, '%Y-%m-%d %p %H:%i') AS API_LOG_ODATE, API_LOG_GNAME, cast(AES_DECRYPT(API_LOG_PHONE, #{AES_KEY}) as CHAR) AS PHONE, API_LOG_TYPE, API_LOG_TF, DATE_FORMAT(API_LOG_DATE, '%Y-%m-%d %p %H:%i') AS API_LOG_DATE, API_LOG_USER FROM fo_api_log WHERE API_LOG_BS = #{bsCode}
AND API_LOG_TYPE = #{type}
AND DATE_FORMAT(API_LOG_DATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
AND API_LOG_GNAME LIKE CONCAT('%', #{gname}, '%')
AND API_LOG_ACNO LIKE CONCAT('%', #{acno}, '%')
ORDER BY API_LOG_DATE DESC, API_LOG_KEY DESC LIMIT ${page},${pageSize}

SELECT (select count(*) from mqtt_action LEFT JOIN bridge_code OPEN ON OPEN.CODE_CODE = MQTT_ACTION_OPEN LEFT JOIN MQTT_LIST ON MQTT_LIST_NUM = MQTT_ACTION_NUM LEFT JOIN bridge_code CODE ON CODE.CODE_CODE = MQTT_LIST_DCOD LEFT JOIN bridge_code CODE2 ON CODE2.CODE_CODE = MQTT_LIST_FLOOR LEFT JOIN ( SELECT BRIDGE_TRAN_ACNO, BRIDGE_TRAN_RSPK, BRIDGE_TRAN_GNAME FROM fo_bridge_tran ) TRAN ON TRAN.BRIDGE_TRAN_ACNO = SUBSTRING_INDEX(MQTT_ACTION_USER, '/', 1) AND TRAN.BRIDGE_TRAN_RSPK = SUBSTRING_INDEX(MQTT_ACTION_USER, '/', -1) LEFT JOIN( SELECT ADMIN_NAME, ADMIN_ID, ADMIN_KEY FROM co_user_admin ) USER ON USER.ADMIN_ID = MQTT_ACTION_USER WHERE MQTT_ACTION_HOTEL = #{bsCode} AND MQTT_ACTION_NUM = #{rono}
AND OPEN.CODE_CODE = #{code}
ORDER BY MQTT_ACTION_REQDATE DESC ) as counts, MQTT_ACTION_KEY, MQTT_ACTION_NUM, DATE_FORMAT(MQTT_ACTION_REQDATE, '%y.%m.%d %T') AS MQTT_ACTION_REQDATE, MQTT_ACTION_ANSDATE, MQTT_ACTION_CMD, MQTT_ACTION_USER, MQTT_ACTION_LOG, IFNULL(CODE.CODE_CONTENT,'') AS DCOD_NAME, IFNULL(CODE2.CODE_CONTENT,'') AS FLOOR_NAME, MQTT_LIST_NAME, IFNULL(TIMESTAMPDIFF(SECOND, MQTT_ACTION_REQDATE, MQTT_ACTION_ANSDATE), '99') AS DIFF, IFNULL(OPEN.CODE_CONTENT, '스마트키') AS OPEN_TYPE, MQTT_ACTION_STATE, TRAN.BRIDGE_TRAN_ACNO, CASE WHEN TRAN.BRIDGE_TRAN_ACNO IS NULL THEN CONCAT(USER.ADMIN_ID, '(관리자)') ELSE CONCAT(TRAN.BRIDGE_TRAN_GNAME, '(손님)') END AS BRIDGE_TRAN_GNAME FROM mqtt_action LEFT JOIN bridge_code OPEN ON OPEN.CODE_CODE = MQTT_ACTION_OPEN LEFT JOIN MQTT_LIST ON MQTT_LIST_NUM = MQTT_ACTION_NUM LEFT JOIN bridge_code CODE ON CODE.CODE_CODE = MQTT_LIST_DCOD LEFT JOIN bridge_code CODE2 ON CODE2.CODE_CODE = MQTT_LIST_FLOOR LEFT JOIN ( SELECT BRIDGE_TRAN_ACNO, BRIDGE_TRAN_RSPK, BRIDGE_TRAN_GNAME FROM fo_bridge_tran ) TRAN ON TRAN.BRIDGE_TRAN_ACNO = SUBSTRING_INDEX(MQTT_ACTION_USER, '/', 1) AND TRAN.BRIDGE_TRAN_RSPK = SUBSTRING_INDEX(MQTT_ACTION_USER, '/', -1) LEFT JOIN( SELECT ADMIN_NAME, ADMIN_ID, ADMIN_KEY FROM co_user_admin ) USER ON USER.ADMIN_ID = MQTT_ACTION_USER WHERE MQTT_ACTION_HOTEL = #{bsCode} AND MQTT_ACTION_NUM = #{rono}
AND OPEN.CODE_CODE = #{code}
ORDER BY MQTT_ACTION_REQDATE DESC LIMIT ${page},${pageSize}

SELECT (select count(*) FROM fo_sync_log LEFT JOIN co_user_admin ON ADMIN_ID = SYNC_LOG_USER WHERE SYNC_LOG_BS = #{bsCode}
AND DATE_FORMAT(SYNC_LOG_DATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
) as counts, SYNC_LOG_KEY, SYNC_LOG_BS, SYNC_LOG_RONO, SYNC_LOG_MSG, DATE_FORMAT(SYNC_LOG_DATE, '%Y-%m-%d %p %H:%i') AS SYNC_LOG_DATE, SYNC_LOG_USER, SYNC_LOG_TYPE,SYNC_LOG_ORIGIN ,SYNC_LOG_CHANGE ,ADMIN_NAME FROM fo_sync_log LEFT JOIN co_user_admin ON ADMIN_ID = SYNC_LOG_USER WHERE SYNC_LOG_BS = #{bsCode}
AND DATE_FORMAT(SYNC_LOG_DATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
order by sync_log_key desc LIMIT ${page},${pageSize}

SELECT (select count(*) from fo_cleaner_log WHERE CLEANER_LOG_BS = #{bsCode}
AND ROOM_CODE_DCOD = #{dcod}
AND DATE_FORMAT(CLEANER_LOG_DATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
AND CLEANER_LOG_NUM LIKE CONCAT('%', #{keyword}, '%')
order by CLEANER_LOG_KEY desc ) as counts, CLEANER_LOG_BS, CLEANER_LOG_KEY, CLEANER_LOG_NUM, ROOM_CODE_FLOOR, ROOM_CODE_HNAM, CLEANER_LOG_ADMIN, CLEANER_LOG_CLEAN, CLEANER_LOG_STATU, DATE_FORMAT(CLEANER_LOG_DATE, '%Y-%m-%d %p %H:%i') AS CLEANER_LOG_DATE, CLEANER_LOG_USER FROM fo_cleaner_log LEFT JOIN FO_ROOM_CODE ON ROOM_CODE_NUM = CLEANER_LOG_NUM WHERE CLEANER_LOG_BS = #{bsCode}
AND ROOM_CODE_DCOD = #{dcod}
AND DATE_FORMAT(CLEANER_LOG_DATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
AND CLEANER_LOG_NUM LIKE CONCAT('%', #{keyword}, '%')
order by CLEANER_LOG_KEY desc LIMIT ${page},${pageSize}

SELECT (select count(*) from fo_sms_log where SMS_LOG_BS = #{bsCode}
AND DATE_FORMAT(SMS_LOG_DATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
AND SMS_LOG_ACNO LIKE CONCAT('%', #{acno}, '%')
AND SMS_LOG_RONO LIKE CONCAT('%', #{rono}, '%')
AND SMS_LOG_GNAME LIKE CONCAT('%', #{gname}, '%')
) AS counts, SMS_LOG_KEY, SMS_LOG_BS, SMS_LOG_STEL, cast(AES_DECRYPT(SMS_LOG_RTEL, #{AES_KEY}) as CHAR) AS SMS_LOG_RTEL, SMS_LOG_TITLE, SMS_LOG_CONT, SMS_LOG_ACNO, SMS_LOG_RONO, SMS_LOG_GNAME, DATE_FORMAT(SMS_LOG_DATE, '%Y-%m-%d %p %H:%i') AS SMS_LOG_DATE, SMS_LOG_USER, SMS_LOG_CPID, SMS_LOG_ASP, SMS_LOG_TYPE, SMS_LOG_TEMP FROM fo_sms_log WHERE SMS_LOG_BS = #{bsCode}
AND DATE_FORMAT(SMS_LOG_DATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
AND SMS_LOG_ACNO LIKE CONCAT('%', #{acno}, '%')
AND SMS_LOG_RONO LIKE CONCAT('%', #{rono}, '%')
AND SMS_LOG_GNAME LIKE CONCAT('%', #{gname}, '%')
order by SMS_LOG_KEY desc LIMIT ${page},${pageSize}

    SELECT
ROOM_CODE_NUM,
ROOM_CODE_CLEAN
FROM FO_ROOM_CODE
WHERE ROOM_CODE_BS = #{bsCode}
AND ROOM_CODE_NUM = #{rono}
