mapper.select:
SELECT (select count(*) from co_user_admin LEFT JOIN BRIDGE_CODE ON CODE_CODE = ADMIN_LEVEL WHERE ADMIN_BS_CODE = #{bsCode}
AND DATE_FORMAT(ADMIN_FDATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
AND ADMIN_NAME LIKE CONCAT('%', #{name}, '%')
AND ADMIN_USE = #{use}
AND ADMIN_LEVEL = #{level}
order by ADMIN_KEY desc ) as counts, ADMIN_KEY, ADMIN_NAME, ADMIN_TEL, ADMIN_ADDR1, ADMIN_ADDR2, ADMIN_ID, ADMIN_PASS, ADMIN_LEVEL, ADMIN_BS_CODE, ADMIN_FUSER, DATE_FORMAT(ADMIN_FDATE, '%Y-%m-%d %p %H:%i') AS ADMIN_FDATE, ADMIN_LUSER, DATE_FORMAT(ADMIN_LDATE, '%Y-%m-%d %p %H:%i') AS ADMIN_LDATE, ADMIN_USE, ADMIN_IMGNAME, ADMIN_IMGPATH, ADMIN_EDATE, CODE_CONTENT FROM co_user_admin LEFT JOIN BRIDGE_CODE ON CODE_CODE = ADMIN_LEVEL WHERE ADMIN_BS_CODE = #{bsCode}
AND DATE_FORMAT(ADMIN_FDATE, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{sdate}, '%Y-%m-%d') AND DATE_FORMAT(#{edate}, '%Y-%m-%d')
AND ADMIN_NAME LIKE CONCAT('%', #{name}, '%')
AND ADMIN_USE = #{dcod}
AND ADMIN_LEVEL = #{level}
order by ADMIN_KEY desc LIMIT ${page},${pageSize}
